    write(pLevel, pLogText, pObject) {
        const color = this.colors[pLevel] || '';
        const coloredText = `${color}${pLogText}${this.reset}`;

        return super.write(pLevel, coloredText, pObject);
    }
}

module.exports = ColoredConsoleLogger;
